INSERT INTO Recipe (id,name, description,vegetarian,serving_for) VALUES (1,'Omelet', 'Omelet with cheese recipe',false,2);
INSERT INTO Ingredient(id,recipe_id,item_name,description,amount,amount_type) VALUES (1,1, 'Egg','Fresh eggs',4,'PIECE');
INSERT INTO Ingredient(id,recipe_id,item_name,description,amount,amount_type) VALUES (2,1, 'Oil','Margarine or sun flower oil',2,'TABLESPOON');
INSERT INTO Ingredient(id,recipe_id,item_name,description,amount,amount_type) VALUES (3,1, 'Cheese','Any type cheese would do.Cheddar is the best',50,'GRAM');
INSERT INTO Ingredient(id,recipe_id,item_name,description,amount,amount_type) VALUES (4,1, 'Salt','Salt',1,'TEASPOON');
INSERT INTO Instruction(id,recipe_id,description,sequence) VALUES (1,1, 'Heat the pan on the stove.',1);
INSERT INTO Instruction(id,recipe_id,description,sequence) VALUES (2,1, 'Add Oil to the pan.',2);
INSERT INTO Instruction(id,recipe_id,description,sequence) VALUES (3,1, 'Slice the cheese into small pieces.',3);
INSERT INTO Instruction(id,recipe_id,description,sequence) VALUES (4,1, 'Add cheese into the pan. Wait for it to melt.',4);
INSERT INTO Instruction(id,recipe_id,description,sequence) VALUES (5,1, 'Break the eggs into the pan carefully',5);
INSERT INTO Instruction(id,recipe_id,description,sequence) VALUES (6,1, 'Cook 3-5 mins on slight fire.',6);
INSERT INTO Instruction(id,recipe_id,description,sequence) VALUES (7,1, 'After that take is from the fire. Add salt. And serve. Bon appetit!',7);
INSERT INTO Recipe (id,name, description,vegetarian,serving_for) VALUES (2,'Oven Baked Salmon', 'This Oven Baked Salmon is brushed with garlic butter, then sprinkled with a delicious brown sugar spice mix for the perfectly baked, flavorful salmon fillet. This is hands-down the best and easiest way to prepare salmon fillet for dinner!',false,4);
INSERT INTO Ingredient(id,recipe_id,item_name,description,amount,amount_type) VALUES (5,2, 'Salmon','A whole Salmon fillet with skin on is recommended!',1,'KILOGRAM');
INSERT INTO Ingredient(id,recipe_id,item_name,description,amount,amount_type) VALUES (6,2, 'Butter','Alternatively olive oil can do too',3,'TABLESPOON');
INSERT INTO Ingredient(id,recipe_id,item_name,description,amount,amount_type) VALUES (7,2, 'Brown sugar','It can be skipped',0.5,'TEASPOON');
INSERT INTO Ingredient(id,recipe_id,item_name,description,amount,amount_type) VALUES (8,2, 'Garlic','Garlic to have unique taste',1,'PIECE');
INSERT INTO Ingredient(id,recipe_id,item_name,description,amount,amount_type) VALUES (9,2, 'Italian seasoning','It can be replaced by other veggies. Oregon or rosemary ',1,'TABLESPOON');
INSERT INTO Instruction(id,recipe_id,description,sequence) VALUES (8,2, 'Place the salmon on a piece of foil set on a rimmed baking sheet. Fold the sides slightly up around the salmon, to catch any dripping butter. Combine the minced garlic with the melted butter. Brush about half of it over the salmon.',1);
INSERT INTO Instruction(id,recipe_id,description,sequence) VALUES (9,2, 'Combine the spices, then sprinkle them evenly over the salmon. Drizzle with the remaining garlic butter.',2);
INSERT INTO Instruction(id,recipe_id,description,sequence) VALUES (10,2, 'Bake the salmon at 375°F for about 15-20 minutes, depending on your preferred doneness. The fish is done when it flakes easily, but is still glossy/shiny – not dull. See further below for some temperature references.',3);
INSERT INTO Recipe (id,name, description,vegetarian,serving_for) VALUES (3,'Shrimp Kebab', 'This shrimp kebab recipe is perfectly grilled with garlic lemon butter marinade.',false,4);
INSERT INTO Ingredient(id,recipe_id,item_name,description,amount,amount_type) VALUES (10,3, 'Shrimp','Any type of shrimps would be recommended .Frozen, shelled, deveined and tail-on shrimps.',500,'GRAM');
INSERT INTO Ingredient(id,recipe_id,item_name,description,amount,amount_type) VALUES (11,3, 'Garlic','A well dried garlic',1,'PIECE');
INSERT INTO Ingredient(id,recipe_id,item_name,description,amount,amount_type) VALUES (12,3, 'Butter','Replaceable with olive oil',2,'TABLESPOON');
INSERT INTO Ingredient(id,recipe_id,item_name,description,amount,amount_type) VALUES (13,3, 'Lemon','A good big juicy lemon',1,'PIECE');
INSERT INTO Ingredient(id,recipe_id,item_name,description,amount,amount_type) VALUES (14,3, 'Cayenne Pepper','Peppers to decorate with veggies',3,'PIECE');
INSERT INTO Ingredient(id,recipe_id,item_name,description,amount,amount_type) VALUES (15,3, 'Parsley','Decoration vegetables',3,'PIECE');
INSERT INTO Instruction(id,recipe_id,description,sequence) VALUES (11,3, 'Coat and marinate the garlic butter mixture on the shrimp, thread them on skewers or bamboo sticks.',1);
INSERT INTO Instruction(id,recipe_id,description,sequence) VALUES (12,3, 'Use indirect heat, it will take 2-3 minutes to cook the entire shrimp, on both sides.',2);
INSERT INTO Recipe (id,name, description,vegetarian,serving_for) VALUES (4,'Tofu stir-fry', 'Get children to help you chop the vegetables and make the sauce for this colourful, simple tofu stir-fry. It is perfect when you need a quick and easy family meal',true,3);
INSERT INTO Ingredient(id,recipe_id,item_name,description,amount,amount_type) VALUES (16,4, 'Soy Sauce','Low-sodium',3,'TABLESPOON');
INSERT INTO Ingredient(id,recipe_id,item_name,description,amount,amount_type) VALUES (17,4, 'Honey','Honey',1.5,'TABLESPOON');
INSERT INTO Ingredient(id,recipe_id,item_name,description,amount,amount_type) VALUES (18,4, 'white wine vinegar','white wine vinegar',1,'TABLESPOON');
INSERT INTO Ingredient(id,recipe_id,item_name,description,amount,amount_type) VALUES (19,4, 'tofu','tofu, cut into chunks',300,'GRAM');
INSERT INTO Ingredient(id,recipe_id,item_name,description,amount,amount_type) VALUES (20,4, 'sunflower oil','sunflower oil',2,'TABLESPOON');
INSERT INTO Ingredient(id,recipe_id,item_name,description,amount,amount_type) VALUES (21,4, 'garlic','garlic cloves, sliced',2,'PIECE');
INSERT INTO Ingredient(id,recipe_id,item_name,description,amount,amount_type) VALUES (22,4, 'ginger','ginger',50,'GRAM');
INSERT INTO Ingredient(id,recipe_id,item_name,description,amount,amount_type) VALUES (23,4, 'carrot','medium size carrots, sliced into thin batons',2,'PIECE');
INSERT INTO Ingredient(id,recipe_id,item_name,description,amount,amount_type) VALUES (24,4, 'broccoli','broccoli, cut into small florets',300,'GRAM');
INSERT INTO Ingredient(id,recipe_id,item_name,description,amount,amount_type) VALUES (25,4, 'red pepper','red pepper, sliced into strips',1,'PIECE');
INSERT INTO Ingredient(id,recipe_id,item_name,description,amount,amount_type) VALUES (26,4, 'cornflour','cornflour',1,'TABLESPOON');
INSERT INTO Ingredient(id,recipe_id,item_name,description,amount,amount_type) VALUES (27,4, 'spring onion','spring onion, sliced',1,'PIECE');
INSERT INTO Ingredient(id,recipe_id,item_name,description,amount,amount_type) VALUES (28,4, 'sesame','sesame seeds',2,'TABLESPOON');
INSERT INTO Ingredient(id,recipe_id,item_name,description,amount,amount_type) VALUES (29,4, 'coriander','small bunch of coriander, chopped',50,'GRAM');
INSERT INTO Instruction(id,recipe_id,description,sequence) VALUES (13,4,'Stir together the soy sauce, honey and vinegar in a bowl, then set aside.',1);
INSERT INTO Instruction(id,recipe_id,description,sequence) VALUES (14,4,'Pat the tofu chunks dry, then season well. Heat half the oil in a wok or large frying pan over a high heat. Fry the tofu for 5 mins until golden, stirring occasionally. Add the garlic and ginger and fry for 1 min more. Tip the tofu mixture into a bowl, pour over half the soy dressing and set aside.',2);
INSERT INTO Instruction(id,recipe_id,description,sequence) VALUES (15,4,'Add the remaining oil to the wok. Add the vegetables and cook for 5 mins until beginning to turn golden, then add a good splash of water and cook for 3-5 mins more until tender.',3);
INSERT INTO Instruction(id,recipe_id,description,sequence) VALUES (16,4,'Stir the cornflour and 1 tbsp water into the remaining soy dressing, then tip into the wok, allow to bubble for 30 seconds, stirring to coat everything in the sauce. Sprinkle over the spring onion, sesame seeds and coriander. Serve with rice.',4);
INSERT INTO Recipe (id,name, description,vegetarian,serving_for) VALUES (5,'Creamy broccoli gnocchi', 'Whip up this tasty vegetarian gnocchi in just 15 minutes. Broccoli, peas and pine nuts combine with crème fraîche and mustard for a tasty midweek meal',true,2);
INSERT INTO Ingredient(id,recipe_id,item_name,description,amount,amount_type) VALUES (30,5, 'crème fraîche','crème fraîche',3,'TABLESPOON');
INSERT INTO Ingredient(id,recipe_id,item_name,description,amount,amount_type) VALUES (31,5, 'Dijon mustard','Dijon mustard',3,'TEASPOON');
INSERT INTO Ingredient(id,recipe_id,item_name,description,amount,amount_type) VALUES (32,5, 'Lemon','zested and juiced',0.5,'PIECE');
INSERT INTO Ingredient(id,recipe_id,item_name,description,amount,amount_type) VALUES (33,5, 'broccoli','long-stem broccoli, each cut into 3 pieces',200,'GRAM');
INSERT INTO Ingredient(id,recipe_id,item_name,description,amount,amount_type) VALUES (34,5, 'gnocchi','fresh gnocchi',350,'GRAM');
INSERT INTO Ingredient(id,recipe_id,item_name,description,amount,amount_type) VALUES (35,5, 'olive oil','olive oil',2,'TABLESPOON');
INSERT INTO Ingredient(id,recipe_id,item_name,description,amount,amount_type) VALUES (36,5, 'pea','frozen peas',100,'GRAM');
INSERT INTO Ingredient(id,recipe_id,item_name,description,amount,amount_type) VALUES (37,5, 'nut','toasted pine nuts',1,'TABLESPOON');
INSERT INTO Instruction(id,recipe_id,description,sequence) VALUES (17,5,'Mix together the crème fraîche, mustard and lemon juice. Set aside. Bring a large pan of salted water to the boil. Once boiling, add the broccoli and cook for 3 mins, then tip in the gnocchi. Cook until the gnocchi begins to float to the surface, this will only take 1 min so keep an eye on them, then drain.',1);
INSERT INTO Instruction(id,recipe_id,description,sequence) VALUES (18,5,'Heat the oil in a large non-stick frying pan and, once hot, tip in the gnocchi, broccoli and frozen peas. Toss around in the hot oil for 1 min, then stir through the crème fraîche mixture. Season to taste. Once hot, spoon into bowls and top with the lemon zest and pine nuts.',21);
